package day4;

public class HomeWork {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Write a program to swap two numbers without a third variable
		// input : int a = 5, int b = 9
		// output: int a = 9, int b = 5

		// Note do not use any third variable

		int a = 10;
		int b = 9;

		System.out.println(a);
		System.out.println(b);

		a = a + b; // a = 19 b = 9
		b = a - b; // a = 19 b = 10
		a = a - b; // a = 9 b = 10

		System.out.println(a);
		System.out.println(b);

		// pseudocode

		// 2 variable
		// 3 variable not be used
		// hint operators can be used
		// + -

	}

}
